mapper.select:
select 

from system_user_permission
where user_id = #{userId,jdbcType=BIGINT}
  and permission_id = #{permissionId,jdbcType=BIGINT}

mapper.delete:
delete from system_user_permission
where user_id = #{userId,jdbcType=BIGINT}
  and permission_id = #{permissionId,jdbcType=BIGINT}

mapper.insert:
insert into system_user_permission (user_id, permission_id, desc
  )
values (#{userId,jdbcType=BIGINT}, #{permissionId,jdbcType=BIGINT}, #{desc,jdbcType=VARCHAR}
  )

mapper.update:
update system_user_permission


    desc = #{desc,jdbcType=VARCHAR},


where user_id = #{userId,jdbcType=BIGINT}
  and permission_id = #{permissionId,jdbcType=BIGINT}

update system_user_permission
set desc = #{desc,jdbcType=VARCHAR}
where user_id = #{userId,jdbcType=BIGINT}
  and permission_id = #{permissionId,jdbcType=BIGINT}
